# typ logiczny - prawda lub fałsz
print("True/False")
czy_znasz_Pythona = True  # zawsze z duzej
print(czy_znasz_Pythona)
print(type(czy_znasz_Pythona))  # <class 'bool'>
print(int(czy_znasz_Pythona))  # zamiana bool na int - dostajemy 1 (True)

czy_znasz_Pythona = False
print(int(czy_znasz_Pythona))  # 0 - fałsz (False)

x = 1
print(bool(x))  # True
x = 100
print(bool(x))  # True - wszystko różne od 0 bedzie True
x = -10
print(bool(x))  # True

a = 14
b = 3
print(f"Wynik porównania {a} > {b}", a > b)  # Wynik porównania 14 > 3 True
print(f"Wynik porównania {a} < {b}", a < b)
print(f"Wynik porównania {a} == {b}", a == b)  # porównanie czy a równa sie b
print(f"Wynik porównania {a} != {b}", a != b)  # != - rózne
#
# Wynik porównania 14 > 3 True
# Wynik porównania 14 < 3 False
# Wynik porównania 14 == 3 False
# Wynik porównania 14 != 3 True
